/**
 * A shape to display a dot.
 *
 * [example usage](./examples/layer-breakpoint.html)
 */
export default class Dot extends BaseShape {
  getClassName() { return 'dot'; }

  // @TODO rename : confusion between accessors and meta-accessors
  _getAccessorList() {
    return { cx: 0, cy: 0, r: 3, color: '#000000'Â };
  }

  render() {
    if (this.$el) { return this.$el; }

    this.$el = document.createElementNS(this.ns, 'circle');

    return this.$el;
  }

  update(renderingContext, datum) {
    const cx = renderingContext.timeToPixel(this.cx(datum));
    const cy = renderingContext.valueToPixel(this.cy(datum));
    const r  = this.r(datum);
    const color = this.color(datum);

    const visible = (cx + r >= renderingContext.minX &&
                     cx - r <= renderingContext.maxX);
    if (!visible) {
      this.$el.setAttributeNS(null, 'visibility', 'hidden');
      return;
    } else {
      this.$el.setAttributeNS(null, 'visibility', 'visible');
    }

    this.$el.setAttributeNS(null, 'transform', `translate(${cx}, ${cy})`);
    this.$el.setAttributeNS(null, 'r', r);
    this.$el.style.fill = color;
  }

  // x1, x2, y1, y2 => in pixel domain
  inArea(renderingContext, datum, x1, y1, x2, y2) {
    const cx = renderingContext.timeToPixel(this.cx(datum));
    const cy = renderingContext.valueToPixel(this.cy(datum));

    if ((cx > x1 && cx < x2) && (cy > y1 && cy < y2)) {
      return true;
    }

    return false;
  }
}

import BaseShape from './base-shape';

/**
 * A shape to display a line. Its main use is as common shape to create a
 * breakpoint visualization. (entity shape)
 *
 * [example usage](./examples/layer-breakpoint.html)
 */
export default class Line extends BaseShape {
  getClassName() { return 'line'; }

  _getAccessorList() {
    return { cx: 0, cy: 0 };
  }

  _getDefaults() {
    return { color: '#000000' };
  }

  render(renderingContext) {
    if (this.$el) { return this.$el; }

    this.$el = document.createElementNS(this.ns, 'path');
    this.$el.setAttributeNS(null, 'shape-rendering', 'geometricPrecision');
    return this.$el;
  }

  encache(data) {

    data = data.slice(0);
    data.sort((a, b) => this.cx(a) < this.cx(b) ? -1 : 1);

    return data;
  }

  _findInData(data, x) {

    // Binary search, demands that data has been encached
    // (i.e. sorted). Returns index of value that matches x. If there
    // is no exact match, returns the index just before where x would
    // appear, unless x would appear as the first element in which
    // case 0 is returned. (So the returned index is always in range
    // for the input array, unless the input array is empty.)

    // Note that x must be in model coordinates (e.g. time), not pixel
    // coords.

    let low = 0;
    let high = data.length - 1;

    while (low <= high) {
      let mid = low + ((high - low) >> 1);
      let value = this.cx(data[mid]);
      if (value < x) {
        low = mid + 1;
      } else if (value > x) {
        high = mid - 1;
      } else {
        return mid;
      }
    }

    if (high < 0) {
      return 0;
    } else {
      return high;
    }
  }

  update(renderingContext, data) { // data array is sorted already

    const before = performance.now();

    const minX = Math.floor(renderingContext.minX);
    const maxX = Math.ceil(renderingContext.maxX);

    console.log("minX = " + minX + ", maxX = " + maxX);

    let instructions = [];
    const n = data.length;

    if (n > 0) {

      // We want to start with the last element to the left of the
      // visible region, and end with the first element beyond the
      // right of it

      let cx0 = renderingContext.timeToPixel.invert(minX);
      let i0 = this._findInData(data, cx0);

      let nextX = renderingContext.timeToPixel(this.cx(data[i0]));

      for (let i = i0; i < n; ++i) {

        const x = nextX;

        if (i + 1 < n) {
          nextX = renderingContext.timeToPixel(this.cx(data[i+1]));
        }

        const y = renderingContext.valueToPixel(this.cy(data[i])) - 0.5;

        instructions.push(`${x},${y}`);

        if (x > maxX) {
          break;
        }
      }
    }          

    console.log("line instructions have " + instructions.length + " elements");

    const instructionStr = 'M' + instructions.join('L');
    this.$el.setAttributeNS(null, 'd', instructionStr);

    this.$el.style.stroke = this.params.color;
    this.$el.style.strokeWidth = 2;
    this.$el.style.fill = 'none';

    const after = performance.now();
    console.log("line update time = " + Math.round(after - before));

    data = null;
  }

  describe(data, t) {
    if (!data.length) return 0;
    let i = this._findInData(data, t);
    const cx = this.cx(data[i]);
    const cy = this.cy(data[i]);
    let unit = "";
    if (typeof(data[i].unit) !== 'undefined') {
      unit = data[i].unit;
    }
    return [{
      cx: cx,
      cy: cy,
      unit: unit
    }];
  }
}

import BaseShape from './base-shape';


/**
 * A shape to display a marker.
 *
 * [example usage](./examples/layer-marker.html)
 */
export default class Marker extends BaseShape {
  getClassName() { return 'marker'; }

  _getAccessorList() {
    return { x: 0, color: '#ff0000' };
  }

  _getDefaults() {
    return {
      handlerWidth: 7,
      handlerHeight: 10,
      displayHandlers: true,
      opacity: 1,
      color: 'red',
    };
  }

  render(renderingContext) {
    if (this.$el) { return this.$el; }

    console.log("marker render");

    const height = renderingContext.height;

    this.$el = document.createElementNS(this.ns, 'g');
    this.$line = document.createElementNS(this.ns, 'line');

    // draw line
    this.$line.setAttributeNS(null, 'x', 0);
    this.$line.setAttributeNS(null, 'y1', 0);
    this.$line.setAttributeNS(null, 'y2', height);
    this.$line.setAttributeNS(null, 'shape-rendering', 'crispEdges');

    this.$el.appendChild(this.$line);

    if (this.params.displayHandlers) {
      this.$handler = document.createElementNS(this.ns, 'rect');

      this.$handler.setAttributeNS(null, 'x', -((this.params.handlerWidth) / 2 ));
      this.$handler.setAttributeNS(null, 'y', renderingContext.height - this.params.handlerHeight);
      this.$handler.setAttributeNS(null, 'width', this.params.handlerWidth);
      this.$handler.setAttributeNS(null, 'height', this.params.handlerHeight);
      this.$handler.setAttributeNS(null, 'shape-rendering', 'crispEdges');

      this.$el.appendChild(this.$handler);
    }

    this.$el.style.opacity = this.params.opacity;

    return this.$el;
  }

  update(renderingContext, datum) {

    const x = renderingContext.timeToPixel(this.x(datum)) - 0.5;

    const visible = (x >= renderingContext.minX &&
                     x <= renderingContext.maxX);
    if (!visible) {
      this.$el.setAttributeNS(null, 'visibility', 'hidden');
      return;
    } else {
      this.$el.setAttributeNS(null, 'visibility', 'visible');
    }

    const color = this.color(datum);

    this.$el.setAttributeNS(null, 'transform', `translate(${x}, 0)`);
    this.$line.style.stroke = color;

    if (this.params.displayHandlers) {
      this.$handler.style.fill = color;
    }

    const after = performance.now();
  }

  inArea(renderingContext, datum, x1, y1, x2, y2) {
    // handlers only are selectable
    const x = renderingContext.timeToPixel(this.x(datum));
    const shapeX1 = x - (this.params.handlerWidth - 1) / 2;
    const shapeX2 = shapeX1 + this.params.handlerWidth;
    const shapeY1 = renderingContext.height - this.params.handlerHeight;
    const shapeY2 = renderingContext.height;

    const xOverlap = Math.max(0, Math.min(x2, shapeX2) - Math.max(x1, shapeX1));
    const yOverlap = Math.max(0, Math.min(y2, shapeY2) - Math.max(y1, shapeY1));
    const area = xOverlap * yOverlap;

    return area > 0;
  }
}

import BaseShape from './base-shape';
import TimelineTimeContext from '../core/timeline-time-context';
import LayerTimeContext from '../core/layer-time-context';
import PNGEncoder from '../utils/png.js';

const xhtmlNS = 'http://www.w3.org/1999/xhtml';

export default class Matrix extends BaseShape {

  getClassName() {
    return 'matrix';
  }

  _getAccessorList() {
    // return { y: 0 };
    return {};
  }

  // TODO determine suitable implementations for _getAccessorList and _getDefaults
  _getDefaults() {
    return {
      normalise: 'none',
      mapper: (value => {
        // The mapper accepts a value, which is guaranteed to be in
        // the range [0,1], and returns r, g, b components which are
        // also in the range [0,1]. This example mapper just returns a
        // grey level.
        let level = 1.0 - value;
        return [ level, level, level ];
      }),
      gain: 1.0,
      smoothing: false, // NB with smoothing we get visible joins at tile boundaries
      maxDataUriLength: 32767 // old IE browser limitation, others are more helpful
    };
  }

  render(renderingCtx) {
    console.log("matrix render called");
    if (this.$el) { return this.$el; }
    this.$el = document.createElementNS(this.ns, 'g');
    if (!this.params.smoothing) {
      // for Chrome
      this.$el.setAttributeNS(null, 'image-rendering', 'pixelated');
    }
    console.log("matrix render returning");
    return this.$el;
  }

  _hybridNormalise(gain) {
    return (col => {
      let max = 0.0;
      for (let i = 0; i < col.length; ++i) {
        let value = Math.abs(col[i]);
        if (value > max) {
          max = value;
        }
      }
      let scale = gain;
      if (max > 0.0) {
        scale = scale * (Math.log10(max + 1.0) / max);
      }
      let n = [];
      for (let i = 0; i < col.length; ++i) {
        let value = col[i];
        n.push(value * scale);
      }
      return n;
    });
  }

  _columnNormalise(gain) {
    return (col => {
      let max = 0.0;
      for (let i = 0; i < col.length; ++i) {
        let value = Math.abs(col[i]);
        if (value > max) {
          max = value;
        }
      }
      let scale = gain;
      if (max > 0.0) {
        scale = scale * (1.0 / max);
      }
      let n = [];
      for (let i = 0; i < col.length; ++i) {
        let value = col[i];
        n.push(value * scale);
      }
      return n;
    });
  }      

  _noNormalise(gain) {
    return (col => {
      let n = [];
      for (let i = 0; i < col.length; ++i) {
        let value = col[i];
        n.push(value * gain);
      }
      return n;
    });
  }      

  encache(matrixEntity) {

    const before = performance.now();

    console.log("matrix cache called");

    const height = matrixEntity.getColumnHeight();
    const totalWidth = matrixEntity.getColumnCount();

    // We use one byte per pixel, as our PNG is indexed but
    // uncompressed, and base64 encoding increases that to 4/3
    // characters per pixel. The header and data URI scheme stuff add
    // a further 1526 chars after encoding, which we round up to 1530
    // for paranoia.
    const maxPixels = Math.floor((this.params.maxDataUriLength * 3) / 4 - 1530);
    let tileWidth = Math.floor(maxPixels / height);
    if (tileWidth < 1) {
      console.log("WARNING: Matrix shape tile width of " + tileWidth +
                  " calculated for height " + height +
                  ", using 1 instead: this may exceed maxDataUriLength of " +
                  this.params.maxDataUriLength);
      tileWidth = 1;
    }
    console.log("totalWidth = " + totalWidth + ", tileWidth = " + tileWidth);

    let resources = [];
    let widths = [];

    let normalise = null;

    switch (this.params.normalise) {
    case 'hybrid':
      normalise = this._hybridNormalise(this.params.gain);
      break;
    case 'column':
      normalise = this._columnNormalise(this.params.gain);
      break;
    default:
      normalise = this._noNormalise(this.params.gain);
      break;
    }

    const condition = (col => {
      let n = [];
      for (let i = 0; i < col.length; ++i) {
        if (col[i] === Infinity || isNaN(col[i])) n.push(0.0);
        else n.push(col[i]);
      }
      return n;
    });

    const usualWidth = tileWidth;
    const usualEncoder = new PNGEncoder(usualWidth, height, 256);

    for (let x0 = 0; x0 < totalWidth; x0 += tileWidth) {

      let w = tileWidth;
      if (totalWidth - x0 < tileWidth) {
	w = totalWidth - x0;
      }

      let p = (w === tileWidth ?
               usualEncoder :
               new PNGEncoder(w, height, 256));

      for (let i = 0; i < w; ++i) {

	const x = x0 + i;
        let col = matrixEntity.getColumn(x);
        col = normalise(condition(col));

	for (let y = 0; y < height; ++y) {
          let value = col[y];
          // The value must be in the range [0,1] to pass to the
          // mapper. We also quantize the range, as the PNG encoder
          // uses a 256-level palette.
          if (value < 0) value = 0;
          if (value > 1) value = 1;
          value = Math.round(value * 255) / 255;
          let [ r, g, b ] = this.params.mapper(value);
          if (r < 0) r = 0;
          if (r > 1) r = 1;
          if (g < 0) g = 0;
          if (g > 1) g = 1;
          if (b < 0) b = 0;
          if (b > 1) b = 1;
          const colour = p.color(Math.round(r * 255),
                                 Math.round(g * 255),
                                 Math.round(b * 255),
                                 255);
          const index = p.index(i, y);
	  p.buffer[index] = colour;
	}
      }

      const resource = 'data:image/png;base64,' + p.getBase64();
      resources.push(resource);
      widths.push(w);

      console.log("image " + resources.length + ": length " + resource.length +
		  " (dimensions " + w + " x " + height + ")");
    }

    console.log("drawing complete");

    const after = performance.now();
    console.log("matrix cache time = " + Math.round(after - before));

    return {
      resources: resources,
      tileWidths: widths,
      totalWidth: totalWidth,
      height: height,
      startTime: matrixEntity.getStartTime(),
      stepDuration: matrixEntity.getStepDuration(),
      elements: [] // will be installed in first call to update
    };
  }

  update(renderingContext, cache) {

    const before = performance.now();

    console.log("matrix update called");

    if (!cache.totalWidth || !cache.height ||
	!renderingContext.width || !renderingContext.height) {
      console.log("nothing to update");
      return;
    }

    if (cache.elements.length === 0) {
      console.log("About to add " + cache.resources.length +
		  " image resources to SVG...");
      for (let i = 0; i < cache.resources.length; ++i) {
	const resource = cache.resources[i];
	const elt = document.createElementNS(this.ns, 'image');
	elt.setAttributeNS('http://www.w3.org/1999/xlink', 'href', resource);
        elt.setAttributeNS(null, 'preserveAspectRatio', 'none');
        if (!this.params.smoothing) {
          // for Firefox
	  elt.setAttributeNS(null, 'image-rendering', 'optimizeSpeed');
        }
	elt.addEventListener('dragstart', e => { e.preventDefault(); }, false);
	this.$el.appendChild(elt);
	cache.elements.push(elt);
      }
      console.log("Done that");
    }

    console.log("Render width = " + renderingContext.width);

    let startX = renderingContext.timeToPixel(cache.startTime);
    const drawnWidth = renderingContext.width - startX;
    let widthScaleFactor = drawnWidth / cache.totalWidth;

    if (cache.stepDuration > 0) {
      let totalDuration = cache.stepDuration * cache.totalWidth;
      let endX = renderingContext.timeToPixel(cache.startTime + totalDuration);
      widthScaleFactor = (endX - startX) / cache.totalWidth;
    }

    let widthAccumulated = 0;

    for (let i = 0; i < cache.elements.length; ++i) {
      const elt = cache.elements[i];
      const tileWidth = cache.tileWidths[i];
      const x = startX + widthAccumulated * widthScaleFactor;
      const w = tileWidth * widthScaleFactor;
      const visible = (x + w > 0 && x < renderingContext.maxX);
      if (visible) {
        elt.setAttributeNS(null, 'x', Math.floor(x));
        elt.setAttributeNS(null, 'width', Math.ceil(x + w) - Math.floor(x));
        elt.setAttributeNS(null, 'y', 0);
        elt.setAttributeNS(null, 'height', renderingContext.height);
        elt.setAttributeNS(null, 'visibility', 'visible');
      } else {
        elt.setAttributeNS(null, 'visibility', 'hidden');
      }
      widthAccumulated += tileWidth;
    }

    const after = performance.now();
    console.log("matrix update time = " + Math.round(after - before));
  }
}

import BaseShape from './base-shape';
import ns from '../core/namespace';
import ScaleTickIntervals from '../utils/scale-tick-intervals';


/**
 * A shape to display a vertical scale at the left edge of the visible
 * area of the layer. Scale values are taken from the yDomain of the
 * layer.
 *
 * [example usage](./examples/layer-scale.html)
 */
export default class Scale extends BaseShape {
  getClassName() { return 'scale'; }

  _getDefaults() {
    return {
      background: '#ffffff',
      tickColor: '#000000',
      textColor: '#000000',
      opacity: 1
    };
  }

  render(renderingContext) {
    if (this.$el) { return this.$el; }

    this.$el = document.createElementNS(this.ns, 'g');

    this.$bg = document.createElementNS(ns, 'rect');
    this.$bg.setAttributeNS(null, 'fill', this.params.background);
    this.$bg.setAttributeNS(null, 'x', 0);
    this.$bg.setAttributeNS(null, 'y', 0);
    this.$el.appendChild(this.$bg);

    this.$path = document.createElementNS(ns, 'path');
    this.$path.setAttributeNS(null, 'shape-rendering', 'geometricPrecision');
    this.$path.setAttributeNS(null, 'stroke-width', '0.7');
    this.$path.style.opacity = this.params.opacity;
    this.$path.style.stroke = this.params.tickColor;
    this.$el.appendChild(this.$path);

    this.$labels = [];

    return this.$el;
  }

  update(renderingContext, datum) {

    console.log("scale update");

    const h = renderingContext.height;
    const cy0 = renderingContext.valueToPixel.domain()[0];
    const cy1 = renderingContext.valueToPixel.domain()[1];

    if (typeof(this.lastCy0) !== 'undefined') {
      if (this.lastCy0 === cy0 &&
	  this.lastCy1 === cy1 &&
	  this.lastH === h) {
	console.log("scale unchanged");
	return;
      }
    }
    this.lastCy0 = cy0;
    this.lastCy1 = cy1;
    this.lastH = h;

    console.log("cy0 = " + cy0);
    console.log("cy1 = " + cy1);

    for (let i = 0; i < this.$labels.length; ++i) {
      this.$el.removeChild(this.$labels[i]);
    }
    this.$labels = [];

    const ticks = (new ScaleTickIntervals()).linear(cy0, cy1, 10);

    let maxLength = ticks.reduce((acc, t) => Math.max(acc, t.label.length), 0);

    let scaleWidth = maxLength * 6.5 + 12;

    this.$bg.setAttributeNS(null, 'width', scaleWidth);
    this.$bg.setAttributeNS(null, 'height', h);

    let path = `M${scaleWidth},0L${scaleWidth},${h}`;

    const addLabel = ((text, x, y) => {

      const $label = document.createElementNS(this.ns, 'text');
      $label.classList.add('label');
      $label.style.fontSize = '10px';
      $label.style.lineHeight = '10px';
      $label.style.fontFamily = 'monospace';
      $label.style.fill = this.params.textColor;
      $label.style.opacity = this.params.opacity;
      $label.style.mozUserSelect = 'none';
      $label.style.webkitUserSelect = 'none';
      $label.style.userSelect = 'none';

      $label.setAttributeNS(
	null, 'transform', `matrix(1, 0, 0, -1, ${x}, ${h})`
      );

      $label.setAttributeNS(null, 'y', y);
      const $text = document.createTextNode(text);
      $label.appendChild($text);

      this.$labels.push($label);
      this.$el.appendChild($label);
    });

    const lx = 2;

    let prevy = h + 2;

    for (let i = 0; i < ticks.length; ++i) {

      let y = renderingContext.valueToPixel(ticks[i].value);

      let ly = h - y + 3;

      let showText = true;
      if (ly > h - 8 || ly < 8 || ly > prevy - 20) {
	// not enough space
	showText = false;
      }

      if (!showText) {

	path = path + `M${scaleWidth-5},${y}L${scaleWidth},${y}`;

      } else {

	path = path + `M${scaleWidth-8},${y}L${scaleWidth},${y}`;
	prevy = ly;
	addLabel(ticks[i].label, lx, ly);
      }
    }

    this.$path.setAttributeNS(null, 'd', path);
  }

  /**
   * The scale cannot be selected.
   * @return {Boolean} false
   */
  inArea() { return false; }
}

import BaseShape from './base-shape';


/**
 * A shape to display a segment.
 *
 * [example usage](./examples/layer-segment.html)
 */
export default class Segment extends BaseShape {
  getClassName() { return 'segment'; }

  _getAccessorList() {
    return { x: 0, y: 0, width: 0, height: 1, color: '#000000', opacity: 1 };
  }

  _getDefaults() {
    return {
      displayHandlers: true,
      handlerWidth: 2,
      handlerOpacity: 0.8,
      opacity: 0.6
    };
  }

  render(renderingContext) {
    if (this.$el) { return this.$el; }

    this.$segment = document.createElementNS(this.ns, 'rect');
    this.$segment.classList.add('segment');
    this.$segment.style.opacity = this.params.opacity;
    this.$segment.setAttributeNS(null, 'shape-rendering', 'geometricPrecision');

    const useShortcut = (this.getClassName() == 'segment' && // not a subclass
                         !this.params.displayHandlers);

    if (useShortcut) {

      this.$el = this.$segment;

    } else {

      this.$el = document.createElementNS(this.ns, 'g');
      this.$el.appendChild(this.$segment);

      if (this.params.displayHandlers) {

        this.$leftHandler = document.createElementNS(this.ns, 'rect');
        this.$leftHandler.classList.add('left', 'handler');
        this.$leftHandler.setAttributeNS(null, 'width', this.params.handlerWidth);
        this.$leftHandler.setAttributeNS(null, 'shape-rendering', 'crispEdges');
        this.$leftHandler.style.opacity = this.params.handlerOpacity;
        this.$leftHandler.style.cursor = 'ew-resize';

        this.$rightHandler = document.createElementNS(this.ns, 'rect');
        this.$rightHandler.classList.add('right', 'handler');
        this.$rightHandler.setAttributeNS(null, 'width', this.params.handlerWidth);
        this.$rightHandler.setAttributeNS(null, 'shape-rendering', 'crispEdges');
        this.$rightHandler.style.opacity = this.params.handlerOpacity;
        this.$rightHandler.style.cursor = 'ew-resize';

        this.$el.appendChild(this.$leftHandler);
        this.$el.appendChild(this.$rightHandler);
      }
    }

    return this.$el;
  }

  update(renderingContext, datum) {

    const x = renderingContext.timeToPixel(this.x(datum));
    const y = renderingContext.valueToPixel(this.y(datum));

    const width = renderingContext.timeToPixel(this.x(datum) +
                                               this.width(datum)) - x;

    const height = renderingContext.valueToPixel(this.height(datum));

    this.$segment.setAttributeNS(null, 'x', x);
    this.$segment.setAttributeNS(null, 'y', y);
    this.$segment.setAttributeNS(null, 'width', Math.max(width, 0));
    this.$segment.setAttributeNS(null, 'height', height);

    const visible = (x + width >= renderingContext.minX &&
                     x <= renderingContext.maxX);

    if (!visible) {
      this.$el.setAttributeNS(null, 'visibility', 'hidden');
      return;
    } else {
      this.$el.setAttributeNS(null, 'visibility', 'visible');
    }

    const color = this.color(datum);
    const opacity = this.opacity(datum);

    this.$el.style.opacity = opacity;
    this.$segment.style.fill = color;

    if (this.params.displayHandlers) {
      this.$leftHandler.setAttributeNS(null, 'x', x);
      this.$leftHandler.setAttributeNS(null, 'y', 0);
      this.$leftHandler.setAttributeNS(null, 'height', height);
      this.$leftHandler.style.fill = color;

      this.$rightHandler.setAttributeNS(null, 'x',
                                        x + width - this.params.handlerWidth);
      this.$rightHandler.setAttributeNS(null, 'y', 0);
      this.$rightHandler.setAttributeNS(null, 'height', height);
      this.$rightHandler.style.fill = color;
    }
  }

  inArea(renderingContext, datum, x1, y1, x2, y2) {
    const shapeX1 = renderingContext.timeToPixel(this.x(datum));
    const shapeX2 = renderingContext.timeToPixel(this.x(datum) + this.width(datum));
    const shapeY1 = renderingContext.valueToPixel(this.y(datum));
    const shapeY2 = renderingContext.valueToPixel(this.y(datum) + this.height(datum));

    // http://jsfiddle.net/uthyZ/ - check overlaping area
    const xOverlap = Math.max(0, Math.min(x2, shapeX2) - Math.max(x1, shapeX1));
    const yOverlap = Math.max(0, Math.min(y2, shapeY2) - Math.max(y1, shapeY1));
    const area = xOverlap * yOverlap;

    return area > 0;
  }
}

import BaseShape from './base-shape';

/**
 * Kind of Marker for entity oriented data. Useful to display a grid.
 */
export default class Ticks extends BaseShape {
  _getClassName() {
    return 'tick';
  }

  _getAccessorList() {
    return { time: 0, focused: true, label: '' };
  }

  _getDefaults() {
    return {
      color: 'steelblue',
      focusedOpacity: 0.8,
      defaultOpacity: 0.3,
      labelPosition: 'top',
      shadeSegments: false,
      unconstrained: false // indicates we should always update all
                           // ticks that exist, as the layer is
                           // handling tick generation dynamically
                           // (e.g. in axis layer)
    };
  }

  render(renderingContext) {
    if (this.$el) { return this.$el; }

    this.$el = document.createElementNS(this.ns, 'g');

    return this.$el;
  }

  update(renderingContext, data) {

    const before = performance.now();

    while (this.$el.firstChild) {
      this.$el.removeChild(this.$el.firstChild);
    }

    const ticks = document.createElementNS(this.ns, 'path');
    ticks.setAttributeNS(null, 'fill', 'none');
    ticks.setAttributeNS(null, 'shape-rendering', 'crispEdges');
    ticks.setAttributeNS(null, 'stroke', this.params.color);
    ticks.setAttributeNS(null, 'stroke-width', 2);
    ticks.style.opacity = this.params.opacity;

    this.$el.appendChild(ticks);

    const height = renderingContext.height;

    let instructions = [];

    const n = data.length;

    if (n > 0) {

      let nextX = renderingContext.timeToPixel(this.time(data[0]));
      let oddTick = false;
      let firstTick = true;

      const segmentOpacity = (odd => { return odd ? 0.1 : 0.05; });

      for (let i = 0; i < n; ++i) {

        const datum = data[i];
        const x = nextX;
        const lastTick = (i + 1 >= n);
        oddTick = !oddTick;

        if (!lastTick) {
          nextX = renderingContext.timeToPixel(this.time(data[i+1]));
        }
        if (!this.params.unconstrained) {
          if (x < renderingContext.minX) {
            continue;
          }
          if (!lastTick && (Math.floor(nextX) === Math.floor(x))) {
            continue;
          }
        }

        const opacity = this.focused(datum) ?
              this.params.focusedOpacity : this.params.defaultOpacity;

        instructions.push(`M${x},0L${x},${height}`);

        if (this.params.shadeSegments) {
          if (firstTick) {
            if (x > renderingContext.minX) {
              const segment = document.createElementNS(this.ns, 'rect');
              segment.setAttributeNS(null, 'width', x - renderingContext.minX);
              segment.setAttributeNS(null, 'height', height);
              segment.setAttributeNS(null, 'fill', this.params.color);
              segment.setAttributeNS(null, 'opacity', segmentOpacity(!oddTick));
              segment.setAttributeNS(null, 'transform', `translate(${renderingContext.minX}, 0)`);
              this.$el.appendChild(segment);
            }
          }
          if (lastTick || nextX > x + 1) {
            const segment = document.createElementNS(this.ns, 'rect');
            segment.setAttributeNS(null, 'width', lastTick ? '100%' : (nextX - x));
            segment.setAttributeNS(null, 'height', height);
            segment.setAttributeNS(null, 'fill', this.params.color);
            segment.setAttributeNS(null, 'opacity', segmentOpacity(oddTick));
            segment.setAttributeNS(null, 'transform', `translate(${x}, 0)`);
            this.$el.appendChild(segment);
          }
        }

        const label = this.label(datum);

        if (label && label !== "") {

          // find the next label -- we only need enough space between
          // this tick and that one, not necessarily between this and
          // its (possibly unlabelled) neighbour

          let nextLabelX = x - 1;
          for (let j = i + 1; j < n; ++j) {
            const nextLabel = this.label(data[j]);
            if (nextLabel && nextLabel !== "") {
              nextLabelX = renderingContext.timeToPixel(this.time(data[j]));
              break;
            }
          }

          const estWidth = label.length * 6;
          const enoughSpaceForLabel = (nextLabelX < x || x + estWidth < nextLabelX);

          if (enoughSpaceForLabel) {

            const $label = document.createElementNS(this.ns, 'text');
            $label.classList.add('label');
            const $text = document.createTextNode(label);

            $label.appendChild($text);
            $label.setAttributeNS(null, 'transform', `matrix(1, 0, 0, -1, ${x + 2}, ${height + 2})`);
            // firefox problem here
            // $label.setAttributeNS(null, 'alignment-baseline', 'text-before-edge');

            if (this.params.labelPosition === 'bottom') {
              $label.setAttributeNS(null, 'y', height);
            } else {
              $label.setAttributeNS(null, 'y', '10');
            }

            $label.style.fontSize = '10px';
            $label.style.lineHeight = '10px';
            $label.style.fontFamily = 'monospace';
            $label.style.color = '#676767';
            $label.style.opacity = 0.9;
            $label.style.mozUserSelect = 'none';
            $label.style.webkitUserSelect = 'none';
            $label.style.userSelect = 'none';
/*
            const bg = document.createElementNS(this.ns, 'rect');
            bg.setAttributeNS(null, 'width', '100%');
            bg.setAttributeNS(null, 'height', '100%');
            bg.setAttributeNS(null, 'fill', '#ffffff');
            $label.appendChild(bg);
*/
            this.$el.appendChild($label);
          }
        }

        if (!this.params.unconstrained) {
          if (nextX > renderingContext.maxX) {
            break;
          }
        }

        firstTick = false;
      }
    }

    const d = instructions.join('');
    ticks.setAttributeNS(null, 'd', d);

    const after = performance.now();
    console.log("ticks update time = " + Math.round(after - before) + "ms");
  }
}

import BaseShape from './base-shape';


/**
 * A shape to display dots in a trace visualization (mean / range).
 *
 * [example usage](./examples/layer-trace.html)
 */
export default class TraceDots extends BaseShape {
  getClassName() { return 'trace-dots'; }

  _getAccessorList() {
    return { x: 0, mean: 0, range: 0 };
  }

  _getDefaults() {
    return {
      meanRadius: 3,
      rangeRadius: 3,
      meanColor: '#232323',
      rangeColor: 'steelblue'
    };
  }

  render(renderingContext) {
    if (this.$el) { return this.$el; }
    // container
    this.$el = document.createElementNS(this.ns, 'g');
    // draw mean dot
    this.$mean = document.createElementNS(this.ns, 'circle');
    this.$mean.setAttributeNS(null, 'r', this.params.meanRadius);
    this.$mean.setAttributeNS(null, 'stroke', this.params.meanColor);
    this.$mean.setAttributeNS(null, 'fill', 'transparent');
    this.$mean.classList.add('mean');
    // range dots (0 => top, 1 => bottom)
    this.$max = document.createElementNS(this.ns, 'circle');
    this.$max.setAttributeNS(null, 'r', this.params.meanRadius);
    this.$max.setAttributeNS(null, 'stroke', this.params.rangeColor);
    this.$max.setAttributeNS(null, 'fill', 'transparent');
    this.$max.classList.add('max');

    this.$min = document.createElementNS(this.ns, 'circle');
    this.$min.setAttributeNS(null, 'r', this.params.meanRadius);
    this.$min.setAttributeNS(null, 'stroke', this.params.rangeColor);
    this.$min.setAttributeNS(null, 'fill', 'transparent');
    this.$min.classList.add('min');

    this.$el.appendChild(this.$mean);
    this.$el.appendChild(this.$max);
    this.$el.appendChild(this.$min);

    return this.$el;
  }

  // @TODO use accessors
  update(renderingContext, datum) {
    const mean = this.mean(datum);
    const range = this.range(datum);
    const x = this.x(datum);
    // y positions
    const meanPos = `${renderingContext.valueToPixel(mean)}`;
    this.$mean.setAttributeNS(null, 'transform', `translate(0, ${meanPos})`);

    const halfRange = range / 2;
    const max = renderingContext.valueToPixel(mean + halfRange);
    const min = renderingContext.valueToPixel(mean - halfRange);
    const xPos = renderingContext.timeToPixel(x);

    this.$max.setAttributeNS(null, 'transform', `translate(0, ${max})`);
    this.$min.setAttributeNS(null, 'transform', `translate(0, ${min})`);
    this.$el.setAttributeNS(null, 'transform', `translate(${xPos}, 0)`);
  }

  inArea(renderingContext, datum, x1, y1, x2, y2) {
    const x = renderingContext.timeToPixel(this.x(datum));
    const mean = renderingContext.valueToPixel(this.mean(datum));
    const range = renderingContext.valueToPixel(this.range(datum));
    const min = mean - (range / 2);
    const max = mean + (range / 2);

    if (x > x1 && x < x2 && (min > y1 || max < y2)) {
      return true;
    }

    return false;
  }
}

import BaseShape from './base-shape';


/**
 * A shape to display paths in a trace visualization (mean / range). (entity shape)
 *
 * [example usage](./examples/layer-trace.html)
 */
export default class TracePath extends BaseShape {
  getClassName() { return 'trace-common'; }

  _getAccessorList() {
    return { x: 0, mean: 0, range: 0 };
  }

  _getDefaults() {
    return {
      rangeColor: 'steelblue',
      meanColor: '#232323',
      displayMean: true
    };
  }

  render(renderingContext) {
    if (this.$el) { return this.$el; }
    this.$el = document.createElementNS(this.ns, 'g');
    // range path
    this.$range = document.createElementNS(this.ns, 'path');
    this.$el.appendChild(this.$range);

    // mean line
    if (this.params.displayMean) {
      this.$mean = document.createElementNS(this.ns, 'path');
      this.$el.appendChild(this.$mean);
    }

    return this.$el;
  }

  update(renderingContext, data) {
    // order data by x position
    data = data.slice(0);
    data.sort((a, b) => this.x(a) < this.x(b) ? -1 : 1);

    if (this.params.displayMean) {
      this.$mean.setAttributeNS(null, 'd', this._buildMeanLine(renderingContext, data));
      this.$mean.setAttributeNS(null, 'stroke', this.params.meanColor);
      this.$mean.setAttributeNS(null, 'fill', 'none');
    }

    this.$range.setAttributeNS(null, 'd', this._buildRangeZone(renderingContext, data));
    this.$range.setAttributeNS(null, 'stroke', 'none');
    this.$range.setAttributeNS(null, 'fill', this.params.rangeColor);
    this.$range.setAttributeNS(null, 'opacity', '0.4');

    data = null;
  }

  _buildMeanLine(renderingContext, data) {
    let instructions = data.map((datum, index) => {
      const x = renderingContext.timeToPixel(this.x(datum));
      const y = renderingContext.valueToPixel(this.mean(datum));
      return `${x},${y}`;
    });

    return 'M' + instructions.join('L');
  }

  _buildRangeZone(renderingContext, data) {
    const length = data.length;
    // const lastIndex = data
    let instructionsStart = '';
    let instructionsEnd = '';

    for (let i = 0; i < length; i++) {
      const datum = data[i];
      const mean = this.mean(datum);
      const halfRange = this.range(datum) / 2;

      const x  = renderingContext.timeToPixel(this.x(datum));
      const y0 = renderingContext.valueToPixel(mean + halfRange);
      const y1 = renderingContext.valueToPixel(mean - halfRange);

      const start = `${x},${y0}`;
      const end   = `${x},${y1}`;

      instructionsStart = instructionsStart === '' ?
        start : `${instructionsStart}L${start}`;

      instructionsEnd = instructionsEnd === '' ?
        end : `${end}L${instructionsEnd}`;
    }

    let instructions = `M${instructionsStart}L${instructionsEnd}Z`;
    return instructions;
  }
}

import BaseShape from './base-shape';
import Oversampler from '../utils/oversample';

const xhtmlNS = 'http://www.w3.org/1999/xhtml';

/**
 * A shape to display a waveform. (for entity data)
 *
 * [example usage](./examples/layer-waveform.html)
 *
 * @todo - fix problems with canvas strategy.
 */
export default class Waveform extends BaseShape {
  getClassName() { return 'waveform'; }

  _getAccessorList() {
    return {};
  }

  _getDefaults() {
    return {
      sampleRate: 44100,
      color: '#000000',
      opacity: 1,
      peakCacheBlockSize: 32,
    };
  }

  render(renderingContext) {
    if (this.$el) { return this.$el; }

    this.$el = document.createElementNS(this.ns, 'path');
    this.$el.setAttributeNS(null, 'fill', 'none');
    this.$el.setAttributeNS(null, 'stroke', this.params.color);
    this.$el.style.opacity = this.params.opacity;

    this.factor = 8;
    this.oversampler = new Oversampler(this.factor);

    return this.$el;
  }

  encache(samples) {

    console.log("waveform encache called");

    // The cache is an array of peak caches (holding the min and max
    // values within each block for a given block size) with each peak
    // cache represented as an object with blockSize, min array, and
    // max array properties.
    //
    // For example:
    //    
    // [ {
    //     blockSize: 16,
    //     max: [ 0.7,  0.5, 0.25, -0.1 ],
    //     min: [ 0.5, -0.1, -0.8, -0.2 ]
    //   }, {
    //     blockSize: 32, 
    //     max: [  0.7,  0.25 ],
    //     min: [ -0.1, -0.8  ]
    //   }
    // ]
    //
    // As it happens we are only creating a cache with a single block
    // size at the moment, but it's useful to record that block size
    // in the cache rather than have to fix it here in the shape.

    const before = performance.now();

    const peakCacheFor = ((arr, blockSize) => {

      let peaks = [], troughs = [];

      const len = arr.length;

      for (let i = 0; i < len; i = i + blockSize) {
        let min = arr[i];
        let max = arr[i];
        for (let j = 0; j < blockSize; j++) {
          let sample = arr[i + j];
          if (sample < min) { min = sample; }
          if (sample > max) { max = sample; }
        }
        peaks.push(max);
        troughs.push(min);
      }

      return [ peaks, troughs ];
    });

    // For a single peak cache, experiment suggests smallish block
    // sizes are better. There's no benefit in having multiple layers
    // of cache (e.g. 32 and 512) unless update() can take advantage
    // of both in a single summarise action (e.g. when asked for a
    // read from 310 to 1050, start by reading single samples from 310
    // to 320, then from the 32-sample cache from 320 to 512, then
    // switch to the 512 sample cache, rather than having to read
    // single samples all the way from 310 to 512)... but at the
    // moment it can't. And the more complex logic would carry its own
    // overhead.

    const blockSize = this.params.peakCacheBlockSize;
    let [ peaks, troughs ] = peakCacheFor(samples, blockSize);

    return {
      samples,
      peakCaches: [
        { blockSize,
          max: peaks,
          min: troughs
        }
      ]
    };
  }

  summarise(cache, minX, maxX, pixelToSample) {

    const before = performance.now();

    const samples = cache.samples;

    const px0 = Math.floor(minX);
    const px1 = Math.floor(maxX);

    let peakCache = null;
    let peakCacheBlockSize = 0;

    if (cache && (cache.peakCaches.length > 0)) {

      // Find a suitable peak cache if we have one.

      // "step" is the distance in samples from one pixel to the next.
      // We want the largest cache whose block size is no larger than
      // half this, so as to avoid situations where our step is always
      // straddling cache block boundaries.
      const step = pixelToSample(px0 + 1) - pixelToSample(px0);

      for (var i = 0; i < cache.peakCaches.length; ++i) {
        const blockSize = cache.peakCaches[i].blockSize;
        if (blockSize > peakCacheBlockSize && blockSize <= step/2) {
          peakCache = cache.peakCaches[i];
          peakCacheBlockSize = peakCache.blockSize;
        }
      }
    }

    const sampleRate = this.params.sampleRate;
    let minMax = [];

    for (let px = px0; px < px1; px++) {

      const startSample = pixelToSample(px);
      if (startSample < 0) continue;
      if (startSample >= samples.length) break;

      let endSample = pixelToSample(px + 1);
      if (endSample >= samples.length) endSample = samples.length;
      if (endSample < 0) continue;

      let min = samples[startSample];
      let max = min;

      let ix = startSample;

      if (peakCache && (peakCacheBlockSize > 0)) {

        while (ix < endSample && (ix % peakCacheBlockSize) !== 0) {
          let sample = samples[ix];
          if (sample < min) { min = sample; }
          if (sample > max) { max = sample; }
          ++ix;
        }

        let cacheIx = ix / peakCacheBlockSize;
        const cacheMax = peakCache.max;
        const cacheMin = peakCache.min;

        while (ix + peakCacheBlockSize <= endSample) {
          if (cacheMax[cacheIx] > max) max = cacheMax[cacheIx];
          if (cacheMin[cacheIx] < min) min = cacheMin[cacheIx];
          ++cacheIx;
          ix = ix + peakCacheBlockSize;
        }
      }

      while (ix < endSample) {
        let sample = samples[ix];
        if (sample < min) { min = sample; }
        if (sample > max) { max = sample; }
        ++ix;
      }

      minMax.push([px, min, max]);
    }

    const after = performance.now();
    console.log("waveform summarisation time = " + Math.round(after - before));

    return minMax;
  }

  _updateSummarising(renderingContext, cache, pixelToSample) {

    console.log("waveform updateSummarising");

    const minX = renderingContext.minX;
    const maxX = renderingContext.maxX;

    // get min/max values per pixel
    const minMax = this.summarise(cache, minX, maxX, pixelToSample);
    if (!minMax.length) { return; }

    let instructions = minMax.map(datum => {
      const [ x, min, max ] = datum;
      const y1 = Math.round(renderingContext.valueToPixel(min));
      const y2 = Math.round(renderingContext.valueToPixel(max));
      return `${x},${y1}L${x},${y2}`;
    });

    const d = 'M' + instructions.join('L');
    this.$el.setAttributeNS(null, 'shape-rendering', 'crispEdges');
    this.$el.setAttributeNS(null, 'stroke-width', 1.0);
    this.$el.setAttributeNS(null, 'd', d);
  }

  _updateInterpolating(renderingContext, cache, pixelToSample, sampleToPixel) {

    console.log("waveform updateInterpolating");

    const minX = renderingContext.minX;
    const maxX = renderingContext.maxX;

    const s0 = pixelToSample(minX);
    const s1 = pixelToSample(maxX) + 1;

    const samples = cache.samples;
    const n = samples.length;

    console.log("minX = " + minX + ", maxX = " + maxX + ", s0 = " + s0 + ", s1 = " + s1);

    let instructions = [];

    // Pixel coordinates in this function are *not* rounded, we want
    // to preserve the proper shape as far as possible

    // Add a little square for each sample location

    for (let i = s0; i < s1 && i < n; ++i) {
      if (i < 0) continue;
      const x = sampleToPixel(i);
      const y = renderingContext.valueToPixel(samples[i]);
      instructions.push(`M${x-1},${y-1}h2v2h-2v-2`);
    }

    // Now fill in the gaps between the squares

    const factor = this.factor;
    const oversampled = this.oversampler.oversample(samples, s0, s1 - s0);

    for (let i = 0; i < oversampled.length; ++i) {
      const x = sampleToPixel(s0 + i/factor); // sampleToPixel accepts non-integers
      const y = renderingContext.valueToPixel(oversampled[i]);
      if (i === 0) {
        instructions.push(`M${x},${y}`);
      } else {
        instructions.push(`L${x},${y}`);
      }
    }

    const d = instructions.join('');
    this.$el.setAttributeNS(null, 'shape-rendering', 'geometricPrecision');
    this.$el.setAttributeNS(null, 'stroke-width', 0.6);
    this.$el.setAttributeNS(null, 'd', d);
  }

  update(renderingContext, cache) {

    console.log("waveform update called");

    const before = performance.now();

    const sampleRate = this.params.sampleRate;
    const minX = renderingContext.minX;

    const step = sampleRate * (renderingContext.timeToPixel.invert(minX + 1) -
			       renderingContext.timeToPixel.invert(minX));

    const snapToCacheBoundaries = (step >= this.params.peakCacheBlockSize * 2);

    console.log("waveform update: pixel step = " + step + " samples, snapToCacheBoundaries = " + snapToCacheBoundaries);

    const pixelToSampleSnapped = (pixel => {
      return this.params.peakCacheBlockSize *
	Math.floor ((sampleRate * renderingContext.timeToPixel.invert(pixel)) /
		    this.params.peakCacheBlockSize);
    });
    const pixelToSampleUnsnapped = (pixel => {
      return Math.floor (sampleRate * renderingContext.timeToPixel.invert(pixel));
    });
    const pixelToSample = (snapToCacheBoundaries ?
			   pixelToSampleSnapped :
			   pixelToSampleUnsnapped);

    const sampleToPixel = (sample => {
      // neither snapped nor even rounded to integer pixel
      return renderingContext.timeToPixel(sample / sampleRate);
    });

    if (step > 1.0) {
      this._updateSummarising(renderingContext, cache,
			      pixelToSample);
    } else {
      this._updateInterpolating(renderingContext, cache,
				pixelToSample, sampleToPixel);
    }

    const after = performance.now();
    console.log("waveform update time = " + Math.round(after - before));
  }
}

/**
 * `State` instances are used to define the application logic by precising
 * specific user interaction cases, and how they impact the overal temporal
 * representation. The abstractions extending this base class should be
 * considered as the main interface between the visualization and the
 * application logic. All provided states should be seen as simple examples for
 * rapid prototyping,
 *
 * States manage interactions like zooming, browsing, or editing the timeline.
 * Customized states should extend this BaseState.
 */
export default class BaseState {
  /**
   * Returns timeline tracks collection.
   *
   * @type {TrackCollection}
   */
  constructor(timeline) {
    /**
     * A reference to the timeline on which the state should be installed.
     * @type {Timeline}
     */
    this.timeline = timeline;
  }

  /**
   * Returns timeline tracks collection.
   *
   * @type {TrackCollection<Track>}
   */
  get tracks() {
    return this.timeline.tracks;
  }

  /**
   * Returns all registered layers.
   *
   * @type {Array<Layer>}
   */
  get layers() {
    return this.timeline.tracks.layers;
  }

  /**
   * Called when the timeline is entering the state.
   */
  enter() {}

  /**
   * Called when the timeline is leaving the state.
   */
  exit() {}

  /**
   * Main interface method to override when creating a new `State`. Handle event
   * from mouse or keyboard, should define behavior according to the event
   * (aka. mousedown, mouseup, ...).
   *
   * @param {WaveEvent} e - the event to process.
   * @param {Array} hitLayers - the layers hit by the mouse event (if surface
   * event).
   */
  handleEvent(e, hitLayers) {}
}

import BaseState from './base-state';


/**
 * A state to interact with a breakpoint function, mimicing Max/MSP's
 * breakpoint function interactions.
 *
 * [example usage](./examples/layer-breakpint.html)
 */
export default class BreakpointState extends BaseState {
  constructor(timeline, datumGenerator) {
    super(timeline);

    this.datumGenerator = datumGenerator;
    this.currentEditedLayer = null;
    this.currentTarget = null;
  }

  enter() {}
  exit() {}

  handleEvent(e, hitLayers) {
    switch (e.type) {
      case 'mousedown':
        this.onMouseDown(e, hitLayers);
        break;
      case 'mousemove':
        this.onMouseMove(e, hitLayers);
        break;
      case 'mouseup':
        this.onMouseUp(e, hitLayers);
        break;
    }
  }

  onMouseDown(e, hitLayers) {
    this.mouseDown = true;
    // keep target consistent with mouse down
    this.currentTarget = e.target;
    let updatedLayer = null;

    const layers = hitLayers;

    layers.forEach((layer) => {
      layer.unselect();
      const item = layer.getItemFromDOMElement(e.target);

      if (item === null) {
        // create an item
        const time = layer.timeToPixel.invert(e.x) - this.timeline.offset;
        const value = layer.valueToPixel.invert(layer.params.height - e.y);
        const datum = this.datumGenerator(time, value);

        layer.data.push(datum);
        updatedLayer = layer;
      } else {
        // if shift is pressed, remove the item
        if (e.originalEvent.shiftKey) {
          const data = layer.data;
          const datum = layer.getDatumFromItem(item);
          data.splice(data.indexOf(datum), 1);

          updatedLayer = layer;
        } else {
          this.currentEditedLayer = layer;
          layer.select(item);
        }
      }
    });

    if (updatedLayer) {
      this.timeline.tracks.render(updatedLayer);
      this.timeline.tracks.update(updatedLayer);
    }
  }

  onMouseMove(e) {
    if (!this.mouseDown ||Â !this.currentEditedLayer) { return; }

    const layer = this.currentEditedLayer;
    const items = layer.selectedItems;
    // the loop should be in layer to match select / unselect API
    items.forEach((item) => {
      layer.edit(item, e.dx, e.dy, this.currentTarget);
    });

    layer.update(items);
  }

  onMouseUp(e) {
    this.currentEditedLayer = null;
    this.mouseDown = false;
  }
}

import ns from '../core/namespace';
import BaseState from './base-state';


/**
 * Protools like zoom with zone selection. Press space bar to reset zoom.
 *
 * [example usage](./examples/states-zoom.html)
 *
 * @todo - could also handle `g` and `h` keys to zoom-in, zoom-out.
 */
export default class BrushZoomState extends BaseState {
  constructor(timeline) {
    super(timeline);
  }

  handleEvent(e) {
    switch(e.type) {
      case 'mousedown':
        this.onMouseDown(e);
        break;
      case 'mousemove':
        this.onMouseMove(e);
        break;
      case 'mouseup':
        this.onMouseUp(e);
        break;
      case 'keydown':
        this.onKeyDown(e);
        break;
    }
  }

  onMouseDown(e) {
    this.brushes = [];
    this.startX = e.x;
    // create brush in each containers
    this.tracks.forEach((track) => {
      const interactions = track.$interactions;

      const brush = document.createElementNS(ns, 'rect');
      brush.setAttributeNS(null, 'height', track.height);
      brush.setAttributeNS(null, 'y', 0);
      brush.style.fill = '#787878';
      brush.style.opacity = 0.2;

      interactions.appendChild(brush);

      this.brushes.push(brush);
    });
  }

  onMouseMove(e) {
    // update brush
    const width = Math.abs(e.x - this.startX);
    const x = Math.min(e.x, this.startX);

    this.brushes.forEach((brush) => {
      brush.setAttributeNS(null, 'width', width);
      brush.setAttributeNS(null, 'x', x);
    });
  }

  onMouseUp(e) {
    // remove brush
    this.brushes.forEach((brush) => {
      brush.parentNode.removeChild(brush);
    });

    // update timeContext
    const startX = this.startX;
    const endX = e.x;
    // return if no drag
    if (Math.abs(startX - endX) < 1) { return; }

    const leftX = Math.max(0, Math.min(startX, endX));
    const rightX = Math.max(startX, endX);

    let minTime = this.timeline.timeToPixel.invert(leftX);
    let maxTime = this.timeline.timeToPixel.invert(rightX);

    const deltaDuration = maxTime - minTime;
    const zoom = this.timeline.visibleDuration / deltaDuration;

    this.timeline.offset -= minTime;
    this.timeline.zoom *= zoom;

    this.tracks.update();
  }

  onKeyDown(e) {
    // reset on space bar
    if (e.originalEvent.keyCode === 32) {
      this.timeline.offset = 0;
      this.timeline.zoom = 1;
      this.tracks.update();
    }
  }
}

import scales from '../utils/scales';
import BaseState from './base-state';


/**
 * `CenteredZoomState` is a timeline state mimicing the `Live` zoom interaction. It allows the user to browse the timeline by clicking on a track, and then
 * - moving down to zoom in
 * - moving up to zoom out
 * - moving left to move in time, after
 * - moving right to move in time, before
 *
 * [example usage](./examples/states-zoom.html)
 */
export default class CenteredZoomState extends BaseState {
  constructor(timeline) {
    super(timeline);
    this.currentLayer = null;
    // Set max/min zoom
    // maxZoom: 1px per sample
    // minZoom: 10 000 px per 1 hour
    // with a default to 44.1kHz sample rate
    this.maxZoom = 44100 * 16 / this.timeline.timeContext.pixelsPerSecond;
    this.minZoom = 10000 / 3600 / this.timeline.timeContext.pixelsPerSecond;
  }

  handleEvent(e) {
    switch(e.type) {
      case 'mousedown':
        this.onMouseDown(e);
        break;
      case 'mousemove':
        this.onMouseMove(e);
        break;
      case 'mouseup':
        this.onMouseUp(e);
        break;
    }
  }

  onMouseDown(e) {

    this.initialX = e.x;
    this.initialOffset = this.timeline.timeContext.offset;
    this.initialCenterTime =
      this.timeline.timeContext.timeToPixel.invert(e.x) - this.initialOffset;

    this.initialY = e.y;
    this.initialZoom = this.timeline.timeContext.zoom;

    this.dragMode = 'unresolved';

    this._pixelToExponent = scales.linear()
      .domain([0, 100]) // 100px => factor 2
      .range([0, 1]);
  }

  updateDragMode(e) {

    if (this.dragMode === 'free') {
      return;
    }

    const dx = Math.abs(e.x - this.initialX);
    const dy = Math.abs(e.y - this.initialY);

    const smallThreshold = 10, bigThreshold = 50;

    if (this.dragMode === 'unresolved') {
      if (dy > smallThreshold && dy > dx * 2) {
        this.dragMode = 'vertical';
      } else if (dx > smallThreshold && dx > dy * 2) {
        this.dragMode = 'horizontal';
      } else if (dx > smallThreshold && dy > smallThreshold) {
        this.dragMode = 'free';
      }
    }

    if (this.dragMode === 'vertical' && dx > bigThreshold) {
      this.dragMode = 'free';
    }
    if (this.dragMode === 'horizontal' && dy > bigThreshold) {
      this.dragMode = 'free';
    }
  }

  onMouseMove(e) {
    // prevent annoying text selection when dragging
    e.originalEvent.preventDefault();

    this.updateDragMode(e);

    const timeContext = this.timeline.timeContext;

    let changed = false;

    if (this.dragMode === 'vertical' ||
        this.dragMode === 'free') {

      const exponent = this._pixelToExponent(e.y - this.initialY);

      // -1...1 -> 1/2...2 :
      const targetZoom = this.initialZoom * Math.pow(2, exponent);

      const clampedZoom = Math.min(Math.max(targetZoom, this.minZoom),
                                   this.maxZoom);

      if (timeContext.zoom !== clampedZoom) {
        timeContext.zoom = clampedZoom;
        changed = true;
      }
    }

    // We want to keep the same time under the mouse as we originally
    // had (this.initialCenterTime). We actually need to do this
    // regardless of drag mode -- even if we're only intending to drag
    // vertically (i.e. zooming), we still want to ensure the point
    // under the mouse doesn't wander off
    const timeMovedTo =
          timeContext.timeToPixel(this.initialCenterTime +
                                  timeContext.offset);

    const delta = e.x - timeMovedTo;
    const deltaTime = timeContext.timeToPixel.invert(delta);

    if (deltaTime !== 0) {
      timeContext.offset += deltaTime;
      changed = true;
    }

    console.log("mouse move: time context offset is now " + timeContext.offset);

    // Other possible experiments with centered-zoom-state
    //
    // Example 1: Prevent timeline.offset to be negative
    // timeContext.offset = Math.min(timeContext.offset, 0);
    //
    // Example 2: Keep in container when zoomed out
    // if (timeContext.stretchRatio < 1)Â {
    //   const minOffset = timeContext.timeToPixel.invert(0);
    //   const maxOffset = timeContext.timeToPixel.invert(view.width - timeContext.timeToPixel(timeContext.duration));
    //   timeContext.offset = Math.max(timeContext.offset, minOffset);
    //   timeContext.offset = Math.min(timeContext.offset, maxOffset);
    // }

    if (changed) {
      this.timeline.tracks.update();
    }
  }

  onMouseUp(e) {
    this.dragMode = 'unresolved';
  }
}

import BaseState from './base-state';
