import BaseState from './base-state';
import ns from '../core/namespace';


/**
 * A state to select shapes.
 */
export default class SelectionState extends BaseState {
  constructor(timeline /*, options = {} */) {
    super(timeline /*, options */);

    this.currentLayer = null;
    // need a cached
    this.selectedItems = null;
    this.mouseDown = false;
    this.shiftKey = false;

    this._layerSelectedItemsMap = new Map();
  }

  enter() {

  }

  exit() {
    const containers = this.timeline.containers;

    for (let id in containers) {
      this._removeBrush(containers[id]);
    }
  }

  handleEvent(e) {
    switch (e.type) {
      case 'mousedown':
        this.onMouseDown(e);
        break;
      case 'mousemove':
        this.onMouseMove(e);
        break;
      case 'mouseup':
        this.onMouseUp(e);
        break;
      case 'click':
        this.onClick(e);
        break;
      case 'keydown':
        this.onKey(e);
        break;
      case 'keyup':
        this.onKey(e);
        break;
    }
  }

  _addBrush(track) {
    if (track.$brush) { return; }

    const brush = document.createElementNS(ns, 'rect');
    brush.style.fill = '#686868';
    brush.style.opacity = 0.2;

    track.$interactions.appendChild(brush);
    track.$brush = brush;
  }

  _removeBrush(track) {
    if (track.$brush === null) { return; }

    this._resetBrush(track);
    track.$interactions.removeChild(track.$brush);
    delete track.$brush;
  }

  _resetBrush(track) {
    const $brush = track.$brush;
    // reset brush element
    $brush.setAttributeNS(null, 'transform', 'translate(0, 0)');
    $brush.setAttributeNS(null, 'width', 0);
    $brush.setAttributeNS(null, 'height', 0);
  }

  _updateBrush(e, track) {
    const $brush = track.$brush;
    const translate = `translate(${e.area.left}, ${e.area.top})`;

    $brush.setAttributeNS(null, 'transform', translate);
    $brush.setAttributeNS(null, 'width', e.area.width);
    $brush.setAttributeNS(null, 'height', e.area.height);
  }

  onKey(e) {
    this.shiftKey = e.shiftKey;
  }

  onMouseDown(e) {
    this._currentTrack = this.timeline.getTrackFromDOMElement(e.target);
    if (!this._currentTrack) { return; }

    this._addBrush(this._currentTrack);

    // recreate the map
    this._layerSelectedItemsMap = new Map();
    this._currentTrack.layers.forEach((layer) => {
      this._layerSelectedItemsMap.set(layer, layer.selectedItems.slice(0));
    });
  }

  onMouseMove(e) {
    this._updateBrush(e, this._currentTrack);

    this._currentTrack.layers.forEach((layer) => {
      const currentSelection = layer.selectedItems;
      const currentItems = layer.getItemsInArea(e.area);

      // if is not pressed
      if (!e.originalEvent.shiftKey) {
        layer.unselect(currentSelection);
        layer.select(currentItems);
      } else {
        const toSelect = [];
        const toUnselect = [];
        // use the selection from the previous drag
        const previousSelection = this._layerSelectedItemsMap.get(layer);
        // toUnselect = toUnselect.concat(previousSelectedItems);

        currentItems.forEach((item) => {
          if (previousSelection.indexOf(item) === -1) {
            toSelect.push(item);
          } else {
            toUnselect.push(item);
          }
        });

        currentSelection.forEach((item) => {
          if (
            currentItems.indexOf(item) === -1 &&
            previousSelection.indexOf(item) === -1
          ) {
            toUnselect.push(item);
          }
        });

        layer.unselect(toUnselect);
        layer.select(toSelect);
      }
    });
  }

  onMouseUp(e) {
    this._removeBrush(this._currentTrack);
  }

  onClick(e) {
    if (!this._currentTrack) { return; }

    this._currentTrack.layers.forEach((layer) => {
      let item = layer.getItemFromDOMElement(e.target);

      if (!e.originalEvent.shiftKey) {
        layer.unselect();
      }

      if (item) {
        layer.toggleSelection(item);
      }
    });
  }
}

import BaseState from './base-state';


/**
 * A state to select and edit shapes in a simple way. (kind of plug n play state)
 */
export default class SimpleEditionState extends BaseState {
  constructor(timeline) {
    super(timeline);

    this.currentEditedLayer = null;
    this.currentTarget = null;
  }

  enter() {}
  exit() {}

  handleEvent(e) {
    switch (e.type) {
      case 'mousedown':
        this.onMouseDown(e);
        break;
      case 'mousemove':
        this.onMouseMove(e);
        break;
      case 'mouseup':
        this.onMouseUp(e);
        break;
    }
  }

  onMouseDown(e) {
    // keep target consistent with mouse down
    this.currentTarget = e.target;

    this.layers.forEach((layer) => {
      if (!layer.hasElement(this.currentTarget)) { return; }

      if (!e.originalEvent.shiftKey) {
        layer.unselect();
      }

      const item = layer.getItemFromDOMElement(this.currentTarget);

      if (item === null) { return; }

      this.currentEditedLayer = layer;
      requestAnimationFrame(function() { layer.select(item); });
    });
  }

  onMouseMove(e) {
    if (!this.currentEditedLayer) { return; }

    const layer = this.currentEditedLayer;
    const items = layer.selectedItems;

    layer.edit(items, e.dx, e.dy, this.currentTarget);
    requestAnimationFrame(function() { layer.update(items); });
  }

  onMouseUp(e) {
    this.currentEditedLayer = null;
  }
}

/**
 * Formatting helpers functions.
 */
export default {
  /**
   * Add a `sign` to the left of a given `input` to match `length`
   *
   * @param {String} input - The string to format.
   * @param {String} sign - The character to add to the left.
   * @param {Number} length - The length of the output string.
   */
  padLeft(input, sign, length) {
    input += '';
    while (input.length < length) {
      input = sign + input;
    }
    return input;
  }
};

